import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';
import MovieList from './components/MovieList';
import MovieListHeading from './components/MovieListHeading';
import SearchBox from './components/SearchBox';
import AddFavourites from './components/AddFavourites';
import RemoveFavourites from './components/RemoveFavourites';

const defaultMovies = [
    {
        imdbID: "1",
        Poster: "https://via.placeholder.com/180x270?text=Film+1",
    },
    {
        imdbID: "2",
        Poster: "https://via.placeholder.com/180x270?text=Film+2",
    },
    {
        imdbID: "3",
        Poster: "https://via.placeholder.com/180x270?text=Film+3",
    },
    {
        imdbID: "4",
        Poster: "https://via.placeholder.com/180x270?text=Film+4",
    },
    {
        imdbID: "5",
        Poster: "https://via.placeholder.com/180x270?text=Film+5",
    }
];

const App = () => {
    const [movies, setMovies] = useState(defaultMovies); // Initialiser avec les films par défaut
    const [favourites, setFavourites] = useState([]);
    const [searchValue, setSearchValue] = useState('');

    const getMovieRequest = async (searchValue) => {
        const url = `http://www.omdbapi.com/?s=${searchValue}&apikey=aeb2b829`;

        const response = await fetch(url);
        const responseJson = await response.json();

        if (responseJson.Search) {
            setMovies(responseJson.Search);
        } else {
            setMovies(defaultMovies); // Réinitialiser aux films par défaut si aucune recherche n'est effectuée
        }
    };

    useEffect(() => {
        if (searchValue) {
            getMovieRequest(searchValue);
        } else {
            setMovies(defaultMovies); // Afficher les films par défaut si rien n'est recherché
        }
    }, [searchValue]);

    useEffect(() => {
        const movieFavourites = JSON.parse(
            localStorage.getItem('react-movie-app-favourites')
        );

        if (movieFavourites) {
            setFavourites(movieFavourites);
        }
    }, []);

    const saveToLocalStorage = (items) => {
        localStorage.setItem('react-movie-app-favourites', JSON.stringify(items));
    };

    const addFavouriteMovie = (movie) => {
        const newFavouriteList = [...favourites, movie];
        setFavourites(newFavouriteList);
        saveToLocalStorage(newFavouriteList);
    };

    const removeFavouriteMovie = (movie) => {
        const newFavouriteList = favourites.filter(
            (favourite) => favourite.imdbID !== movie.imdbID
        );

        setFavourites(newFavouriteList);
        saveToLocalStorage(newFavouriteList);
    };

    return (
        <div className='container-fluid movie-app'>
            <div className='row d-flex align-items-center mt-4 mb-4'>
                <MovieListHeading heading='Films' />
                <SearchBox searchValue={searchValue} setSearchValue={setSearchValue} />
            </div>
            <div className='row'>
                <MovieList
                    movies={movies}
                    handleFavouritesClick={addFavouriteMovie}
                    favouriteComponent={AddFavourites}
                />
            </div>
            <div className='row d-flex align-items-center mt-4 mb-4'>
                <MovieListHeading heading='Favoris' />
            </div>
            <div className='row'>
                <MovieList
                    movies={favourites}
                    handleFavouritesClick={removeFavouriteMovie}
                    favouriteComponent={RemoveFavourites}
                />
            </div>
        </div>
    );
};

export default App;
